pragma solidity 0.8.15;

import "./Vault.sol";

abstract contract LeveragedLendingVault is Vault {
    uint256 public targetCollateralRatio;

    uint256 public maxCollateralRatio;

    event TargetCollateralRatioUpdated(uint256 oldTargetCollateralRatio, uint256 newTargetCollateralRatio);

    event MaxCollateralRatioUpdated(uint256 oldMaxCollateralRatio, uint256 newMaxCollateralRatio);

    constructor(
        ERC20 _asset,
        string memory _name,
        string memory _symbol
    ) Vault(_asset, _name, _symbol) {}

    /////  Leverage logic  /////

    function rebalance(uint256 amountToFreeUp, bool totalRampUp) internal {
        uint256 assetBalance = asset.balanceOf(address(this));

        if (assetBalance > 0) {
            supplyToProtocol(assetBalance);
        }

        uint256 borrowed = getBorrowedFromProtocol();
        uint256 supplied = getSuppliedToProtocol();
        uint256 targetBorrow = FixedPointMathLib.mulDivDown(targetCollateralRatio, supplied - borrowed - amountToFreeUp, 1e18 - targetCollateralRatio);

        if (borrowed < targetBorrow) {
            // ramp up
            while(borrowed * 1e18 < targetBorrow * 0.995e18) {
                uint256 toBorrow = targetBorrow - borrowed;
                uint256 maxBorrow = FixedPointMathLib.mulDivDown(supplied, maxCollateralRatio, 1e18) - borrowed;

                if (toBorrow > maxBorrow) {
                    toBorrow = maxBorrow;
                }

                borrowFromProtocol(toBorrow);
                supplyToProtocol(toBorrow);

                if (!totalRampUp) {
                    break;
                }

                borrowed = getBorrowedFromProtocol();
                supplied = getSuppliedToProtocol();
            }
        } else if (borrowed > targetBorrow) {
            // ramp down
            while(borrowed * 1e18 > targetBorrow * 1.005e18) {
                uint256 toRepay = borrowed - targetBorrow;
                uint256 maxRepay = FixedPointMathLib.mulDivDown(supplied, maxCollateralRatio, 1e18) - borrowed;

                if (toRepay > maxRepay) {
                    toRepay = maxRepay;
                }

                redeemFromProtocol(toRepay);
                repayToProtocol(toRepay);

                borrowed = getBorrowedFromProtocol();
                supplied = getSuppliedToProtocol();
            }
        }

        if (amountToFreeUp > 0) {
            redeemFromProtocol(amountToFreeUp);
        }
    }

    function rebalance() public {
        rebalance(0, true);
    }

    /////  Vault hooks  /////

    function onDeposit(uint256) internal override {
        rebalance(0, false);
    }

    function onWithdraw(uint256 assets) internal override {
        rebalance(assets, false);
    }

    function totalAssets() public view override returns (uint256) {
        return asset.balanceOf(address(this)) + getSuppliedToProtocol() - getBorrowedFromProtocol();
    }

    function onHarvest() internal override {
        collectHarvest();
    }

    function afterHarvest() internal override {
        rebalance(0, true);
    }

    /////  Collateral ratios  /////

    function setMaxCollateralRatio(uint256 newMaxCollateralRatio) public onlyManagerOrOwner {
        uint oldMaxCollateralRatio = maxCollateralRatio;

        require(msg.sender == owner || newMaxCollateralRatio < oldMaxCollateralRatio, "Only Owner can raise maxCollateralRatio");

        maxCollateralRatio = newMaxCollateralRatio;
        emit MaxCollateralRatioUpdated(oldMaxCollateralRatio, newMaxCollateralRatio);
    }

    function setTargetCollateralRatio(uint256 newTargetCollateralRatio) public onlyManagerOrOwner {
        require(newTargetCollateralRatio < maxCollateralRatio, "Cannot set targetCollateralRatio over maxCollateralRatio");
        emit TargetCollateralRatioUpdated(targetCollateralRatio, newTargetCollateralRatio);
        targetCollateralRatio = newTargetCollateralRatio;
    }

    /////  Hooks  /////

    /**
     * @notice Get amount of assets supplied to the protocol by the vault
     * @return Assets supplied
     */
    function getSuppliedToProtocol() public virtual view returns (uint256);

    /**
     * @notice Get amount of assets borrowed from the protocol by the vault
     * @return Assets borrowed
     */
    function getBorrowedFromProtocol() public virtual view returns (uint256);

    /**
     * @notice Supply assets to protocol
     * @param amount Assets to supply to the protocol
     */
    function supplyToProtocol(uint256 amount) internal virtual;

    /**
     * @notice Withdraw assets from protocol
     * @param amount Assets to redeem from the protocol
     */
    function redeemFromProtocol(uint256 amount) internal virtual;

    /**
     * @notice Borrow assets from protocol
     * @param amount Assets to borrow from the protocol
     */
    function borrowFromProtocol(uint256 amount) internal virtual;

    /**
     * @notice Repay debt to protocol
     * @param amount Assets to repay to the protocol
     */
    function repayToProtocol(uint256 amount) internal virtual;

    /**
     * @notice Get liquidity availailable in the protocol
     * @return liquidity The diff
     * @dev ignored on this version
     */
    function getProtocolLiquidity() public virtual view returns (uint256);

    /**
     * @notice Get remaining supply cap in the protocol
     * @return remainingSupplyCap
     * @dev ignored on this version
     * @dev should return 0 if the supply cap is exhausted
     */
    function getRemainingProtocolSupplyCap() public virtual view returns (uint256);

    /**
     * @notice Get remaining borrow cap in the protocol
     * @return remainingBorrowCap
     * @dev ignored on this version
     * @dev should return 0 if the borrow cap is exhausted
     */
    function getRemainingProtocolBorrowCap() public virtual view returns (uint256);

    /**
     * @notice Colect rewards token from protocol
     */
    function collectHarvest() internal virtual {}
}","keccak256":"0x8f2522494528b885110c6b705fec5b2b875b2138588941ad6ae71442071a10e6","license":"MIT"},"contracts/citrus-vaults/src/mixins/Vault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import "../../lib/solmate/src/mixins/ERC4626.sol";
import {FixedPointMathLib} from "../../lib/solmate/src/utils/FixedPointMathLib.sol";
import "../../lib/BoringSolidity/contracts/BoringOwnable.sol";

struct HarvestCall {
    address target;
    bytes callData;
}

struct Harvestable {
    address token;
    uint256 amount;
}

struct HarvestCheckpoint {
    // rolling block timestamp
    uint32 blockTimestamp;

    // rolling amount of assets per share
    uint224 assetsPerShare;
}

abstract contract Vault is ERC4626, BoringOwnable {
    using FixedPointMathLib for uint256;

    event UpdateFeeExclusion(address user, bool excluded);
    event UpdateAllowedHarvester(address target, bool allowed);
    event Harvest(uint assetsPerShare, uint sharesToHarvester);

    // @notice The manager is allowed to perform some privileged actions on the vault, 
    address public manager;

    // @notice This address receive all fees captured by this vault
    address public feeTaker;

    // @notice Percentage the user has to pay to withdraw. 1e18 is 100%
    uint256 public withdrawalFee;

    // @notice Harvesting generate . 1e18 is 100%
    uint256 public harvestFee;

    // @notice stores assetsPerShare evolution over time
    // @dev used to calculate yield/apy
    HarvestCheckpoint[] public harvestCheckpoints;

    // @notice addresses that are excluded from fees
    mapping(address => bool) public excludedFromFees;

    // @notice address of contracts that could be called during harvest
    mapping(address => bool) public allowedHarvesters;

    // @notice array of contracts that could be called during harvest
    // @dev only use this to check if a contract should be removed, some disabled harvesters can be in this array
    address[] public _allHarvesters;

    constructor(
        ERC20 _asset,
        string memory _name,
        string memory _symbol
    ) ERC4626(_asset, _name, _symbol) {
        harvestCheckpoints.push(HarvestCheckpoint({
            blockTimestamp: uint32(block.timestamp),
            assetsPerShare: uint224(convertToAssets(10 ** decimals))
        }));
    }

    /////  Harvest  /////

    // modified version of: https://github.com/makerdao/multicall/blob/1e1b44362640820bef92d0ccf5eeee25d9b41474/src/Multicall.sol#L17-L25
    // @dev The caller could steal the harvest but should not be able to steal any of the deposited funds.
    // Stealing the harvest would result in bad PR and users withdrawing their funds without losing their principal.
    function harvest(HarvestCall[] memory calls) public virtual onlyManager returns (bytes[] memory returnData) {
        uint256 balanceBefore = totalAssets();

        onHarvest();

        returnData = new bytes[](calls.length);
        for(uint256 i = 0; i < calls.length; i++) {
            require(allowedHarvesters[calls[i].target], "harvestor not allowed");

            bool success;
            bytes memory ret;

            (success, ret) = calls[i].target.delegatecall(calls[i].callData);

            if (!success) {
                // Next 5 lines from https://ethereum.stackexchange.com/a/83577
                if (ret.length < 68) revert();
                assembly {
                    ret := add(ret, 0x04)
                }
                revert(abi.decode(ret, (string)));
            }

            returnData[i] = ret;
        }

        uint256 balanceAfter = totalAssets();

        require(balanceAfter >= balanceBefore, "negative harvest");
        uint256 diffBalance;
        unchecked {
          diffBalance = balanceAfter - balanceBefore;
        }

        uint256 denom = balanceBefore + (diffBalance.mulDivUp(1e18 - harvestFee, 1e18));
        uint256 harveterShares = totalSupply.mulDivDown(balanceAfter, denom) - totalSupply;

        _mint(feeTaker, harveterShares);

        uint256 assetsPerShare = convertToAssets(10 ** decimals);
        harvestCheckpoints.push(HarvestCheckpoint({
            blockTimestamp: uint32(block.timestamp),
            assetsPerShare: uint224(assetsPerShare)
        }));
        emit Harvest(assetsPerShare, harveterShares);

        afterHarvest();
    }

    // @notice approve a contract to be used during harvesting
    function allowHarvester(address target, bool allowed) public onlyOwner {
        if (allowed) {
            _allHarvesters.push(target);
        }
        allowedHarvesters[target] = allowed;
        emit UpdateAllowedHarvester(target, allowed);
    }

    // @notive get array of all contracts ever allowed
    // @dev could contains harvester that are now disabled
    function allHarvesters() public view returns (address[] memory) {
        return _allHarvesters;
    }

    /////  Checkpoints  /////

    function harvestCheckpointsLength() public view returns (uint256) {
        return harvestCheckpoints.length;
    }

    function invertedHarvestCheckpoints(uint256 index) public view returns (HarvestCheckpoint memory) {
        return harvestCheckpoints[harvestCheckpoints.length - 1 - index];
    }

    function selectHarvestCheckpoints(uint256 start, uint256 end) public view returns (HarvestCheckpoint[] memory) {
        uint256 length = end - start + 1;

        HarvestCheckpoint[] memory arr = new HarvestCheckpoint[](length);
        for (uint256 i = 0; i < length; i++) {
            arr[i] = harvestCheckpoints[start + i];
        }
        return arr;
    }

    function selectInvertedHarvestCheckpoints(uint256 start, uint256 end) public view returns (HarvestCheckpoint[] memory) {
        uint256 length = end - start + 1;
        uint256 lastIndex = harvestCheckpoints.length - 1;

        HarvestCheckpoint[] memory arr = new HarvestCheckpoint[](length);
        for (uint256 i = 0; i < length; i++) {
            arr[i] = harvestCheckpoints[lastIndex - i];
        }
        return arr;
    }

    function yield() public view virtual returns (uint256 diffTimestamp, int256 diffAssetsPerShare) {
        uint lastHarvestCheckpointsIndex = harvestCheckpoints.length - 1;

        if (lastHarvestCheckpointsIndex < 1) {
            return (1 days, 0);
        }

        HarvestCheckpoint memory lastCheckpoint = harvestCheckpoints[lastHarvestCheckpointsIndex];
        HarvestCheckpoint memory beforeLastCheckpoint = harvestCheckpoints[lastHarvestCheckpointsIndex - 1];

        unchecked { // timestamp is allowed to overflow
            diffTimestamp = lastCheckpoint.blockTimestamp - beforeLastCheckpoint.blockTimestamp;
        }
        diffAssetsPerShare = int256(((int224(lastCheckpoint.assetsPerShare) - int224(beforeLastCheckpoint.assetsPerShare)) * 1e18) / int224(beforeLastCheckpoint.assetsPerShare));
    }

    /////  Admin  /////

    function setManager(address _manager) public onlyOwner {
        manager = _manager;
    }

    /////  Fees  /////

    function excludeFromFees(address user, bool exclude) public onlyOwner {
        excludedFromFees[user] = exclude;
        emit UpdateFeeExclusion(user, exclude);
    }

    function setFeeTaker(address _feeTaker) public onlyOwner {
        feeTaker = _feeTaker;
    }

    function setWithdrawalFee(uint256 _withdrawalFee) public onlyOwner {
        withdrawalFee = _withdrawalFee;
    }

    function setHarvestFee(uint256 _harvestFee) public onlyOwner {
        harvestFee = _harvestFee;
    }

    /////  ERC4626 hooks  /////

    function afterDeposit(uint256 assets, uint256) internal override {
        onDeposit(assets);
    }

    function beforeWithdraw(uint256 assets, uint256 shares) internal override {
        if (!excludedFromFees[msg.sender]) {
            _mint(feeTaker, shares.mulDivDown(withdrawalFee, 1e18));
        }
        onWithdraw(assets);
    }

    function previewWithdraw(uint256 assets) public view override returns (uint256) {
        uint256 _withdrawalFee = withdrawalFee;

        if (excludedFromFees[msg.sender]) {
            _withdrawalFee = 0;
        } 

        uint256 supply = totalSupply; // Saves an extra SLOAD if totalSupply is non-zero.

        uint shares = supply == 0 ? assets : assets.mulDivUp(supply, totalAssets());
        return shares.mulDivUp(1e18, 1e18 - _withdrawalFee);
    }

    function previewRedeem(uint256 shares) public view override returns (uint256) {
        uint256 _withdrawalFee = withdrawalFee;

        if (excludedFromFees[msg.sender]) {
            _withdrawalFee = 0;
        } 

        return convertToAssets(shares).mulDivDown(1e18 - _withdrawalFee, 1e18);
    }

    ///// Modifiers  /////

    modifier onlyManager() {
        require(msg.sender == manager, "Only manager is allowed to call");
        _;
    }

    modifier onlyManagerOrOwner() {
        require(msg.sender == manager || msg.sender == owner, "Only manager or Owner is allowed to call");
        _;
    }

    /////  Hooks  /////

    function onDeposit(uint256 assets) internal virtual {}

    function onWithdraw(uint256 assets) internal virtual {}

    function onHarvest() internal virtual {}

    function afterHarvest() internal virtual {}

    function harvestable() public virtual view returns (Harvestable[] memory);
}","keccak256":"0xd55923020cec0f50db738fc2f009e28a4f08ceb81cad0e0646ada01a7b1bce10","license":"MIT"}},"version":1}
