// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.5.17;

/**
 * @title UnitrollerInterface
 */
contract UnitrollerInterface {
    /**
     * @notice Administrator for this contract
     */
    address public admin;

    /**
     * @notice Whether or not the Fuse admin has admin rights
     */
    bool public fuseAdminHasRights;

    /**
     * @notice Whether or not the admin has admin rights
     */
    bool public adminHasRights;

    /**
     * @notice Accepts new implementation of comptroller. msg.sender must be pendingImplementation
     * @dev Admin function for new implementation to accept it's role as implementation
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _acceptImplementation() external returns (uint256);
}

